<?php

namespace AsyncTweets\TweetBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * TweetRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class TweetRepository extends EntityRepository
{
    public function getWithUsers()
    {
        $qb = $this->createQueryBuilder('t')
                
            ->addSelect('user')
            ->innerJoin('t.user', 'user')
            
            ->orderBy('t.id', 'DESC')
        ;
        
        return $qb->getQuery()->getResult();
    }
    
    public function getWithUsersAndMedias($lastTweetId = null)
    {
        $qb = $this->createQueryBuilder('t')
                
            ->addSelect('user')
            ->innerJoin('t.user', 'user')
            
            ->addSelect('medias')
            ->leftJoin('t.medias', 'medias')
            
            ->orderBy('t.id', 'ASC')
            ->setFirstResult(0)
            ->setMaxResults(10)
        ;
        
        if (! is_null($lastTweetId))
        {
            $qb = $qb
                ->where(
                    $qb->expr()->gt('t.id', $lastTweetId)
                )
            ;
        }
        
        return $qb->getQuery()->getResult();
    }
    
    public function countPendingTweets($lastTweetId = null)
    {
        $qb = $this->createQueryBuilder('t');
        
        $qb = $qb
            ->add('select', $qb->expr()->count('t.id'))
        ;
        
        if (! is_null($lastTweetId))
        {
            $qb = $qb
                ->where(
                    $qb->expr()->gt('t.id', $lastTweetId)
                )
            ;
        }
        
        # return COUNT() result
        return $qb->getQuery()->getSingleScalarResult();
    }
}
